import React, { useState } from "react";
import logo from "./logo.svg";
import "./App.css";
import { useQuery, useMutation } from "@apollo/react-hooks";
import gql from "graphql-tag";
import "antd/dist/antd.css";
import { Button } from "antd";
import { FormApp } from "./FormApp";

//Query per prendere i dati dal database
const GET_PERIOD = gql`
  query period {
    periodTypes(scopeGroupId: 20124) {
      items {
        id
        displayName
      }
    }
  }
`;

//Query per rimuovere i dati dal database
const REMOVE_PERIOD = gql`
  mutation($siteGroupId: Long!, $periodTypeIds: String!) {
    deletePeriodType(siteGroupId: $siteGroupId, periodTypeIds: $periodTypeIds)
  }
`;
//Query per aggiungere dati al Database
const ADD_PERIOD = gql`
  mutation($periodType: InputPeriodType!) {
    createPeriodType(periodType: $periodType) {
      id
      displayName
    }
  }
`;

const EDIT_PERIOD = gql`
  mutation($periodTypeIds: String!) {
    editPerdiodType(periodType: $periodType)
    id
  }
`;

interface Props {}

const App: React.FC<Props> = () => {
  const [dataForm, setDataForm] = useState({
    groupId: 20128,
    id: Number,
    name: "",
    code: "",
    title: "",
    description: "",
    flat: false,
    threshold: false,
    flexible: false,
    position: 0,
    reportOverride: false,
    workflowStatus: 0
  });

  const { loading, error, data } = useQuery(GET_PERIOD, {
    //Quando si aggiunge o si elimina un elemento il fetchPolicy serve ad aggiornare automaticamente la pagina
    fetchPolicy: "cache-and-network"
  });
  console.log(data);

  const [removePeriod] = useMutation(REMOVE_PERIOD, {
    refetchQueries: ["period"]
  });

  const [addTodo] = useMutation(ADD_PERIOD, {
    refetchQueries: ["period"]
  });

  const [editPeriod] = useMutation(EDIT_PERIOD, {});

  console.log("Questo è il log della mutation");
  console.log(ADD_PERIOD);

  const onChange = () => {
    localStorage.setItem("Summary", JSON.stringify(dataForm));
    console.log(JSON.stringify(dataForm));
    addTodo({
      variables: {
        periodType: {
          groupId: 20128,
          name: dataForm.name,
          code: dataForm.code,
          title: JSON.stringify({ en_US: dataForm.title }),
          description: JSON.stringify({ en_US: dataForm.description }),
          flat: dataForm.flat,
          threshold: dataForm.threshold,
          flexible: dataForm.flexible,
          position: 0,
          reportOverride: false,
          workflowStatus: 0
        }
      }
    });
  };

  const onSave = () => {};
  if (loading) return <p>Loading ...</p>;

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.tsx</code> and save to reload.
        </p>
        <h3>
          Hello
          <ul>
            {data.periodTypes.items.map((value: any) => (
              <li key={value.id}>
                {value.displayName}
                <Button
                  onClick={(e: any) => {
                    removePeriod({
                      variables: {
                        siteGroupId: 20128,
                        periodTypeIds: value.id
                      }
                    });
                  }}
                >
                  Remove
                </Button>
                <Button
                  onClick={(e: any) => {
                    editPeriod({
                      variables: {
                        siteGroupId: 20128,
                        periodTypeIds: value.id
                      }
                    });
                  }}
                >
                  Edit
                </Button>
              </li>
            ))}
          </ul>
          <FormApp
            onSubmit={onChange}
            dataForm={dataForm}
            setDataForm={setDataForm}
            onSave={onSave}
          ></FormApp>
        </h3>

        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
};

export default App;
