import React from "react";
import logo from "./logo.svg";
import "./App.css";
import { useQuery, useMutation } from "@apollo/react-hooks";
import gql from "graphql-tag";

const App = () => {
  const GET_PERIOD = gql`
    query period {
      periodTypes(scopeGroupId: 20124) {
        items {
          id
          displayName
        }
      }
    }
  `;

  const ADD_PERIOD = gql`
    mutation($periodType: InputPeriodType!) {
      createPeriodType(periodType: $periodType) {
        id
        displayName
      }
    }
  `;

  function Hello() {
    const { loading, error, data } = useQuery(GET_PERIOD, {
      variables: {}
    });
    console.log(data);
    if (loading) return <p>Loading ...</p>;
    return (
      <h1>
        Hello {data.periodTypes.items.map((value: any) => value.displayName)}!
      </h1>
    );
  }

  const [addTodo] = useMutation(ADD_PERIOD);

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.tsx</code> and save to reload.
        </p>
        <Hello></Hello>

        <form
          onSubmit={() =>
            addTodo({
              variables: {
                periodType: {
                  groupId: 20128,
                  name: "Nome",
                  code: "",
                  title: "",
                  description: "",
                  flat: false,
                  threshold: false,
                  flexible: false,
                  position: 0,
                  reportOverride: false,
                  workflowStatus: 0
                }
              }
            })
          }
        >
          <label>First name: </label>
          <input></input>
          <br></br>
          <label>Last name: </label>
          <input></input>
          <br></br>
          <input type="submit" value="Submit"></input>
        </form>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
};

export default App;
