import React, { useState } from "react";
import logo from "./logo.svg";
import "./App.css";
import { useQuery, useMutation } from "@apollo/react-hooks";
import gql from "graphql-tag";
import "antd/dist/antd.css";
import { Form, Radio, Button } from "antd";
import { Input } from "antd";

// type form = {
//   name: string;
//   code: string;
//   title: string;
//   description: string;
//   flat: false;
//   threshold: false;
//   flexible: false;
//   position: 0;
//   reportOverride: false;
//   workflowStatus: 0;
// };

const App = () => {
  const GET_PERIOD = gql`
    query period {
      periodTypes(scopeGroupId: 20124) {
        items {
          id
          displayName
        }
      }
    }
  `;

  const [dataForm, setDataForm] = useState({
    groupId: 20128,
    name: "",
    code: "",
    title: "",
    description: ""
    // flat: false,
    // threshold: false,
    // flexible: false,
    // position: 0,
    // reportOverride: false,
    // workflowStatus: 0
  });

  const ADD_PERIOD = gql`
    mutation($periodType: InputPeriodType!) {
      createPeriodType(periodType: $periodType) {
        id
        displayName
      }
    }
  `;

  function Hello() {
    const { loading, error, data } = useQuery(GET_PERIOD, {
      variables: {}
    });
    console.log(data);
    if (loading) return <p>Loading ...</p>;
    return (
      <h1>
        Hello {data.periodTypes.items.map((value: any) => value.displayName)}!
      </h1>
    );
  }

  const [addTodo] = useMutation(ADD_PERIOD);
  console.log("Questo è il log della mutation");
  console.log(ADD_PERIOD);

  // const onChangeInput = (event: any) => {
  //   console.log(event.target.name);
  //   setDataForm({
  //     ...dataForm,
  //     [event.target.name]: event.target.value
  //   });
  // };

  const onChange = () => {
    localStorage.setItem("Summary", JSON.stringify(dataForm));
    console.log(JSON.stringify(dataForm));
    addTodo({
      variables: {
        periodType: {
          groupId: 20128,
          name: dataForm.name,
          code: dataForm.code,
          title: dataForm.title,
          description: dataForm.description
          // flat: dataForm.flat,
          // threshold: dataForm.threshold,
          // flexible: dataForm.flexible,
          // position: 0,
          // reportOverride: false,
          // workflowStatus: 0
        }
      }
    });
  };
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.tsx</code> and save to reload.
        </p>
        <Hello></Hello>

        <Form>
          <label>Name: </label>
          <Input
            name="name"
            value={dataForm.name}
            onChange={(event: any) => {
              setDataForm({
                ...dataForm,
                name: event.target.value
              });
            }}
          ></Input>
          <br></br>
          <label>Code:</label>
          <Input
            name="code"
            value={dataForm.code}
            onChange={(event: any) => {
              console.log(dataForm.name);
              setDataForm({
                ...dataForm,
                code: event.target.value
              });
            }}
          ></Input>
          <br></br>
          <label>Title: </label>
          <Input
            name="title"
            value={dataForm.title}
            onChange={(event: any) => {
              setDataForm({
                ...dataForm,
                title: event.target.value
              });
            }}
          ></Input>
          <br></br>
          <label>description: </label>
          <Input
            name="description"
            value={dataForm.description}
            onChange={(event: any) => {
              setDataForm({
                ...dataForm,
                description: event.target.value
              });
            }}
          ></Input>
          <br></br>
          {/* <label>flat: </label>
          <Radio
            value={dataForm.flat}
            name="flat"
            defaultChecked={false}
            onChange={(value: any) => {
              setDataForm({
                ...dataForm,
                flat: value
              });
            }}
          ></Radio>
          <br></br>
          <label>threshold </label>
          <Radio
            name="threshold"
            defaultChecked={false}
            value={dataForm.threshold}
            onChange={(value: any) => {
              setDataForm({
                ...dataForm,
                threshold: value
              });
            }}
          ></Radio>
          <br></br>
          <label>flexible </label>
          <Radio
            name="flexible"
            value={dataForm.flexible}
            defaultChecked={false}
            onChange={(value: any) => {
              setDataForm({
                ...dataForm,
                flexible: value
              });
            }}
          ></Radio> */}
          <br></br>
          <br></br>
          <Button type="primary" onClick={onChange}>
            Primary
          </Button>
        </Form>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
};

export default App;
