import React, { useState } from "react";
import logo from "./logo.svg";
import "./App.css";
import { useQuery, useMutation } from "@apollo/react-hooks";
import gql from "graphql-tag";

// type form = {
//   name: string;
//   code: string;
//   title: string;
//   description: string;
//   flat: false;
//   threshold: false;
//   flexible: false;
//   position: 0;
//   reportOverride: false;
//   workflowStatus: 0;
// };

const [dataForm, setDataForm] = useState({
  groupId: 20128,
  name: "",
  code: "",
  title: "",
  description: "",
  flat: false,
  threshold: false,
  flexible: false,
  position: 0,
  reportOverride: false,
  workflowStatus: 0
});
const App = () => {
  const GET_PERIOD = gql`
    query period {
      periodTypes(scopeGroupId: 20124) {
        items {
          id
          displayName
        }
      }
    }
  `;

  const ADD_PERIOD = gql`
    mutation($periodType: InputPeriodType!) {
      createPeriodType(periodType: $periodType) {
        id
        displayName
      }
    }
  `;

  function Hello() {
    const { loading, error, data } = useQuery(GET_PERIOD, {
      variables: {}
    });
    console.log(data);
    if (loading) return <p>Loading ...</p>;
    return (
      <h1>
        Hello {data.periodTypes.items.map((value: any) => value.displayName)}!
      </h1>
    );
  }

  const [addTodo] = useMutation(ADD_PERIOD);

  const onChangeInput = (event: any) => {
    console.log(event.target.name);
    setDataForm({
      ...dataForm,
      [event.target.name]: event.target.value
    });
  };
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.tsx</code> and save to reload.
        </p>
        <Hello></Hello>

        <form
          onSubmit={() =>
            addTodo({
              variables: {
                periodType: {
                  groupId: 20128,
                  name: "",
                  code: "",
                  title: "",
                  description: "",
                  flat: false,
                  threshold: false,
                  flexible: false,
                  position: 0,
                  reportOverride: false,
                  workflowStatus: 0
                }
              }
            })
          }
        >
          <label>Name: </label>
          <input value={dataForm.name} onChange={onChangeInput}></input>
          <br></br>
          <label>Code:</label>
          <input value={dataForm.code} onChange={onChangeInput}></input>
          <br></br>
          <label>Title: </label>
          <input value={dataForm.title} onChange={onChangeInput}></input>
          <br></br>
          <label>description: </label>
          <input value={dataForm.description} onChange={onChangeInput}></input>
          <br></br>
          <label>flat: </label>
          <input value="" onChange={onChangeInput}></input>
          <br></br>
          <label>threshold </label>
          <input></input>
          <br></br>
          <label>flexible </label>
          <input></input>
          <br></br>
          <label>position: </label>
          <input value={dataForm.position} onChange={onChangeInput}></input>
          <br></br>
          <input type="submit" value="Submit"></input>
        </form>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
};

export default App;
